mapper.select:
select u.case_id as caseId, u.customer_id as customerId, u.budget as budget, u.source as source, u.destination as destination, u.status as status, u.operator as operator, u.customer_name as chineseName, u.customer_mobile as mobile, u.customer_email as email, u.customer_wechat as wechat, u.customer_qq as qq, u.creat_time as creatTime, u.reason as reason, u.last_comment as comment, u.requirement as requirement from tm_case u where u.isdel = 0
and customer_id = #{customerId}
and customer_name like '%${chineseName}%'
and case_id = #{caseId}
and budget = #{budget}
and destination = #{destination}
and source = #{source}
and operator = #{operator}
and status = #{status}
and last_comment like '%${comment}%'
and requirement like '%${requirement}%'
and customer_email like '%${email}%'
and customer_mobile like '%${mobile}%'
order by u.creat_time desc limit #{start},#{length}

select count(u.case_id) from tm_case u
where
u.isdel = 0

	and customer_id = #{customerId}


	and customer_name like '%${chineseName}%'


	and case_id = #{caseId}


	and budget = #{budget}


	and destination = #{destination}


	and source = #{source}


	and operator = #{operator}


	and status = #{status}


	and last_comment  like '%${comment}%'


	and requirement like '%${requirement}%'


	and customer_email  like '%${email}%'


	and customer_mobile like '%${mobile}%'

select count(u.case_id) from tm_case u
where
u.isdel = 0 and u.customer_id = #{customerId}

select
	customer_portal_id as portalId,
	email as email,
	mobilephone as mobilephone,
	qq ,
	wechat,
	customer_id as customerId
from tm_customer


		customer_portal_id = #{portalId}


		 or email = #{email} 


		or mobilephone = #{mobile} 


		or qq = #{qq} 


		or wechat = #{wechat}


limit 1
