mapper.select:
SELECT * FROM ( select u.customer_id as customer_id, u.customer_name as name, u.country as cname, u.language as vlanguage, u.creat_time as creat_time, v.chinese as country, x.chinese as language, u.isdel as isdel from tm_customer u left join tm_parameter v on u.country = v.para_value left join tm_parameter x on u.language = x.para_value where u.isdel = 0 and v.isdel = 0 and x.isdel = 0
and ( u.customer_name like '%${pojo.seachValue}%' or v.chinese like '%${pojo.seachValue}%' or x.chinese like '%${pojo.seachValue}%' )
order by u.creat_time ) table_alias limit #{b},#{e}
