mapper.select:
SELECT * FROM ( select u.userid as userid, u.name as name, u.loginname as loginname, u.pwd as pwd, u.upttime as upttime, u.delflag as delflag from demo_user u where u.delflag = 0
and ( u.loginname like '%${pojo.seachValue}%' or u.name like '%${pojo.seachValue}%' )
order by u.upttime desc ) table_alias limit #{b},#{e}
